            Part2.Text = "--- Part Two ---\r\nRealizing the error of his ways, Santa has switched to a better model of determining whether a string is naughty or nice. None of the old rules apply, as they are all clearly ridiculous.\r\n\r\nNow, a nice string is one with all of the following properties:\r\n\r\nIt contains a pair of any two letters that appears at least twice in the string without overlapping, like xyxy (xy) or aabcdefgaa (aa), but not like aaa (aa, but it overlaps).\r\nIt contains at least one letter which repeats with exactly one letter between them, like xyx, abcdefeghi (efe), or even aaa.\r\nFor example:\r\n\r\nqjhvhtzxzqqjkmpb is nice because is has a pair that appears twice (qj) and a letter that repeats with exactly one letter between them (zxz).\r\nxxyxx is nice because it has a pair that appears twice and a letter that repeats with one between, even though the letters used by each rule overlap.\r\nuurcxstgmygtbstg is naughty because it has a pair (tg) but no repeat with a single letter between them.\r\nieodomkazucvgmuy is naughty because it has a repeating letter with one between (odo), but no pair that appears twice.\r\nHow many strings are nice under these new rules?";
            Part2.Input = Part1.Input;
        }

        public override void DoPart1()
        {
            string[] names = Part1.Input.Split(Environment.NewLine, StringSplitOptions.RemoveEmptyEntries);
            int niceCount = 0;

            string[] vowel = new string[] { "a", "e", "i", "o", "u" };
            string bad = "ab,cd,pq,xy";

            foreach (string name in names)
            {
                int vowels = 0;
                int twice = 0;
                int badStrings = 0;

                char lastChar = ' ';
                for (int i = 0; i < name.Length; i++)
                {
                    char c = name[i];
                    if(i > 0)
                        lastChar = name[i - 1];

                    switch(char.ToLower(c))
                    {
                        case 'a':
                        case 'e':
                        case 'i':
                        case 'o':
                        case 'u':
                            vowels++;
                            break;

                        case 'b':
                            if (lastChar == 'a')
                                badStrings++;
                            break;
                        case 'd':
                            if(lastChar == 'c')
                                badStrings++;
                            break;
                        case 'q':
                            if (lastChar == 'p')
                                badStrings++;
                            break;
                        case 'y':
                            if (lastChar == 'x')
                                badStrings++;
                            break;
                    }

                    if(c == lastChar)
                        twice++;
                }

                if (vowels >= 3 && twice >= 1 && badStrings == 0)
                {
                    niceCount++;
                }
            }

            Part1.Output = $"Nice = {niceCount}";
        }

        public override void DoPart2()
        {
            string[] names = Part2.Input.Split(Environment.NewLine, StringSplitOptions.RemoveEmptyEntries);
            int niceCount = 0;

            foreach (string name in names)
            {
                var pair = false;
                var repeat = false;

                for (int i = 0; i < name.Length-2; i++)
                {
                    if (name[i] != name[i + 1] && name[i] == name[i + 2])
                        pair = true;
                    
                    for (int j = i + 2; j < name.Length - 1; j++)
                    {
                        if (i == j)
                            continue;

                        if (name[i] == name[j] && name[i + 1] == name[j + 1])
                        {
                            repeat = true;
                        }
                    }
                }

                if(pair && repeat)
                {
                    niceCount++;
                }
            }

            Part2.Output = $"Nice = {niceCount}";
        }
    }
}
